    public static final JSONObject API_ERROR_DESCRIPTION_JSON = new JSONObject(API_ERRORS).getJSONObject("descriptions");

    public static String getDescription(String errorMessage) {
        String description = null;
        try {
            description = API_ERROR_DESCRIPTION_JSON.getString(errorMessage);
        } catch (JSONException e) {
            for (String key : API_ERROR_DESCRIPTION_JSON.keySet()) {
                if (key.contains("%d")) {
                    String regexKey = key.replace("%d", "([0-9]+)");
                    Pattern pattern = Pattern.compile(regexKey);
                    Matcher matcher = pattern.matcher(errorMessage);
                    if (matcher.find()) {
                        description = API_ERROR_DESCRIPTION_JSON.getString(key);
                        description = description.replace("%d", matcher.group(1));
                        break;
                    }
                }
            }
        }
        return description;
    }

    public static int getInt(String errorMessage) {
        Pattern pattern = Pattern.compile("([0-9]+)");
        Matcher matcher = pattern.matcher(errorMessage);
        if (matcher.find()) {
            return Integer.parseInt(matcher.group(1));
        }
        return -1;
    }

    public static boolean contains(String errorMessageX, String errorMessage) {
        if (errorMessageX.contains("_X_") || errorMessageX.contains("_X")) {
            errorMessageX = errorMessageX.replace("_X", "_([0-9]+)");
        } else if (errorMessageX.contains("X_")) {
            errorMessageX = errorMessageX.replace("X_", "([0-9]+)_");
        }
        Pattern pattern = Pattern.compile(errorMessageX);
        Matcher matcher = pattern.matcher(errorMessage);
        return matcher.matches();
    }

